157bd4b1.88a82.func:
const {
  url,
  method,
  headers,
  body,
  bodyType,
} = inputs;

let stringifiedBody = null;

if (method !== 'GET' && body) {
  try {
    if (bodyType === 'json') {
      stringifiedBody = JSON.stringify(body);
    } else if (bodyType === 'x-www-form-urlencoded') {
      const paramsArray = Object.keys(body).map((key) => `${encodeURIComponent(key)}=${encodeURIComponent(body[key])}`);
      stringifiedBody = paramsArray.join('&');
    } else {
      return [1, {
        error: {
          code: 'unknownBodyType',
          message: `Unknown body type: ${bodyType}`,
          rawError: null,
        },
      }];
    }
  } catch (error) {
    return [1, {
      error: {
        code: 'bodyNotStringifiable',
        message: 'Could not stringify/process the request body',
        rawError: error,
      },
    }];
  }
}
const postRequest = new Request(url);
const reqHeaders = new Headers();

if (Array.isArray(headers)) {
  headers
    .filter(({ header, value }) => typeof header === 'string' && typeof value === 'string' && header.length && value.length)
    .forEach(({ header, value }) => reqHeaders.append(header, value));
}

let contentTypeIncluded = false;
let acceptIncluded = false;

if (Array.isArray(headers)) {
  headers.forEach((h) => {
    if (!contentTypeIncluded) contentTypeIncluded = String(h.header).toLowerCase() === 'content-type';
    if (!acceptIncluded) acceptIncluded = String(h.header).toLowerCase() === 'accept';
  });
}

if (bodyType === 'json' && !contentTypeIncluded) {
  reqHeaders.append('Content-Type', 'application/json');
}

if (bodyType === 'x-www-form-urlencoded' && !contentTypeIncluded) {
  reqHeaders.append('Content-Type', 'application/x-www-form-urlencoded');
}

if (!acceptIncluded) reqHeaders.append('Accept', 'application/json');

const init = {
  method,
  headers: reqHeaders,
  mode: 'cors',
  body: stringifiedBody,
};

try {
  const response = await fetch(postRequest, init);
  let resBodyParsed;
  let resBodyRaw;
  if (response) {
    try {
      resBodyParsed = await response.json();
      resBodyRaw = JSON.stringify(resBodyParsed);
    } catch (e) {
      resBodyParsed = null;
      resBodyRaw = null;
    }

    const resHeaders = {};

    // eslint-disable-next-line no-restricted-syntax
    for (const header of response.headers.entries()) {
      const [key, value] = header;
      resHeaders[key] = value;
    }

    if (!response.ok && ((response.status >= 400 && response.status <= 451) || (response.status >= 500 && response.status <= 524))) {
      return [1, {
        statusCode: response.status,
        statusText: response.statusText,
        resHeaders,
        resBodyRaw,
        resBodyParsed,
        resBody: resBodyParsed,
      }];
    }

    if (!response.ok) { throw response; }
    return [0, {
      statusCode: response.status,
      statusText: response.statusText,
      resHeaders,
      resBodyRaw,
      resBodyParsed,
      resBody: resBodyParsed,
    }];
  }
} catch (error) {
  let errorStr = String(error);
  if (errorStr === 'TypeError: Failed to fetch') {
    errorStr += '. Does the server allow CORS?';
    return [2, {
      error: {
        code: 'requestFailed',
        status: error.status,
        message: errorStr,
        rawError: error,
      },
    }];
  }
  if (error.status) {
    return [2, {
      error: {
        code: 'httpError',
        message: 'Error was thrown because the response\'s status code was not 2XX, 4XX, 5XX or valid one within that range. ',
        rawError: error,
      },
    }];
  }
  return [2, {
    error: {
      code: 'unknown',
      message: `Unknown error occurred while making the ${method} request to ${url}.`,
      rawError: error,
    },
  }];
}

19b93bf3.89208.func:
const { request, contextId, supersonic } = inputs;
  const { resourceName, id, parameters = {} } = request;
  const { getComponentContextProp } = supersonic.context;
  const { getResourceConfig } = supersonic.api.data;
  const { prepareExecutable } = supersonic.api.formulas || {};
  const get = (p, o) => {
      const isNumber = (v) => typeof v === 'number';
      const isBoolean = (v) => typeof v === 'boolean';
      const isNull = (v) => v === null;
      const isString = (v) => typeof v === 'string';
      return p.reduce((xs, x) => xs && (xs[x] || isNumber(xs[x]) || isBoolean(xs[x]) || isNull(xs[x]) || isString(xs[x])) ? xs[x] : undefined, o);
  };
  class ErrorWithData extends Error {
      constructor(type, message, status, dataOrDriverRawError) {
          super(message);
          this.type = type;
          this.setStatus(status, dataOrDriverRawError);
          this.setData(dataOrDriverRawError);
      }
      setStatus(status, driverRawError) {
          if (status) {
              this.status = status;
          }
          else if (driverRawError && driverRawError.response) {
              this.status = driverRawError.response.status || -1;
          }
          else {
              this.status = -1;
          }
      }
      setData(dataOrDriverRawError) {
          const axiosErrorData = dataOrDriverRawError && dataOrDriverRawError.response && dataOrDriverRawError.response.data;
          this.data = axiosErrorData || dataOrDriverRawError;
      }
  }
  const importRecord = (record, mappingFields = []) => {
      const importedData = {};
      mappingFields.forEach((field) => {
          if (field && field.mappingExpression && prepareExecutable) {
              try {
                  const expression = prepareExecutable(JSON.parse(field.mappingExpression));
                  importedData[field.propertyName] = expression.execute({ source: { record } });
              }
              catch (error) {
                  throw new Error('An error occured while executing mapping formula.');
              }
          }
          else if (field && field.mapping && Array.isArray(field.mapping)) {
              const value = get(field.mapping, record);
              if (value !== undefined) {
                  importedData[field.propertyName] = value;
              }
          }
      });
      return importedData;
  };
  const adapterPrefix = 'adapter';
  const [prefix, boundResource] = (resourceName || '').split(':');
  const isAdapter = prefix === adapterPrefix;
  const getActualResourceName = () => {
      if (isAdapter) {
          const adapterProp = getComponentContextProp(contextId.rootContextId, boundResource, contextId, true);
          return get(['mapping', 'value'], adapterProp) || get(['value'], adapterProp) || resourceName;
      }
      return resourceName;
  };
  const getMapping = () => isAdapter
      ? get(['mapping', 'fields'], getComponentContextProp(contextId.rootContextId, boundResource, contextId, true))
      : null;
  const getAdapterFields = () => {
      if (isAdapter) {
          const adapterProp = getComponentContextProp(contextId.rootContextId, boundResource, contextId, true);
          return ['identifier', 'authConfig', 'additionalInputs'].reduce((adapterFields, field) => {
              const value = get(['mapping', field], adapterProp) || get([field], adapterProp);
              return value ? { ...adapterFields, [field]: value } : adapterFields;
          }, {});
      }
      return {};
  };
  // TODO: should be handled in odata driver in a future version of
  // api-configurator using string schema format field set to "uuid"
  const uuidMatcher = /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
  const fixIdentifierValue = (value) => value && typeof value === 'string' && !uuidMatcher.test(value) ? `'${value}'` : value;
  const prepareQuery = (query, resourceType) => {
      if (resourceType === 'odata' && query.identifier && Object.keys(query.identifier).length > 1) {
          return {
              ...query,
              identifier: Object.keys(query.identifier).reduce((acc, key) => ({ ...acc, [key]: fixIdentifierValue(query.identifier[key]) }), {}),
          };
      }
      return query;
  };
  const mappingFields = getMapping(resourceName);
  const isMappedAdapter = isAdapter && !!mappingFields;
  const implementsCsrf = (driverConfiguration) => (driverConfiguration.base && driverConfiguration.base.fetchCsrfToken) || false;
  async function getRequestAdapterInfo(btpDestinationName, driverConfiguration) {
      const { sapBtp } = supersonic.api;
      const btpDriverAdapter = sapBtp.getDriverAdapter && sapBtp.getDriverAdapter(btpDestinationName);
      if (btpDriverAdapter) {
          return { adapter: btpDriverAdapter, isProxyAdapter: true };
      }
      const serviceImplementsCsrf = implementsCsrf(driverConfiguration);
      const appRouterCsrfAdapter = !serviceImplementsCsrf && sapBtp.getAppRouterCsrfAdapter && (await sapBtp.getAppRouterCsrfAdapter());
      if (appRouterCsrfAdapter) {
          return { adapter: appRouterCsrfAdapter, isProxyAdapter: false };
      }
      return { adapter: null };
  }
  async function makeRequest() {
      const resourceConfig = { ...getResourceConfig(getActualResourceName(resourceName)) };
      const { driverConfiguration = {} } = resourceConfig;
      const resourceType = resourceConfig.type || driverConfiguration.type;
      if (!resourceConfig) {
          throw new Error(`Couldn't find data resource with given name: ${resourceName}.`);
      }
      // Load a local data resource
      if (resourceType === 'local') {
          const { appId } = await supersonic.api.app.getAppId();
          const key = `local-resource:${appId}:${resourceName}:${id}`;
          const { data: record } = await supersonic.api.asyncStorage.getItem({ key });
          if (record != null && typeof record === 'object' && typeof record.id !== 'undefined') {
              return record;
          }
          throw new Error(`Record ${resourceName} with ID ${id} not found`);
      }
      if (resourceType === 'agdb') {
          const data = await supersonic.resource.find(resourceConfig.agdb_id, id, {});
          return data && data.value && data.value.object;
      }
      const parseBtpDestinationName = () => {
          if (!driverConfiguration.base || !driverConfiguration.base.baseURL)
              return undefined;
          const { baseURL } = driverConfiguration.base;
          if (baseURL.source !== 'expression')
              return undefined;
          const match = baseURL.key.formula.match(/connectedSystem\.btp\.applicationBaseUrl \+ '(.+)'/);
          if (!match)
              return undefined;
          return match[1];
      };
      const btpDestinationName = parseBtpDestinationName();
      let requestAdapter;
      if (supersonic.api.data.drivers && resourceType in supersonic.api.data.drivers) {
          const driver = supersonic.api.data.drivers[resourceType];
          if (!driver.execute) {
              throw new Error(`Invalid or missing driver for Resource ${resourceName}`);
          }
          let { authConfig } = request;
          if (resourceType === 'firestore') {
              try {
                  const { firebase } = supersonic.api;
                  const currentUser = await new Promise((resolve, reject) => {
                      const unsubscribe = firebase.auth().onAuthStateChanged((user) => {
                          unsubscribe();
                          resolve(user);
                      }, reject);
                  });
                  const token = await currentUser.getIdToken();
                  authConfig = {
                      type: 'bearer',
                      authToken: token,
                  };
              }
              catch (err) {
                  /* ignore error and attempt query anyway */
              }
          }
          else if (['restd', 'odata', 'uql'].includes(resourceType) && btpDestinationName) {
              try {
                  const { sapBtp } = supersonic.api;
                  const token = await sapBtp.getAccessToken();
                  authConfig = token
                      ? {
                          type: 'bearer',
                          authToken: token,
                      }
                      : undefined;
                  const requestAdapterInfo = await getRequestAdapterInfo(btpDestinationName, resourceConfig.driverConfiguration);
                  requestAdapter = requestAdapterInfo.adapter;
                  let destinationUrl;
                  if (requestAdapter && requestAdapterInfo.isProxyAdapter) {
                      destinationUrl = '';
                  }
                  else {
                      destinationUrl = await sapBtp.resolveDestinationUrl(btpDestinationName);
                  }
                  resourceConfig.driverConfiguration = {
                      ...resourceConfig.driverConfiguration,
                      base: {
                          ...resourceConfig.driverConfiguration.base,
                          baseURL: {
                              source: 'str',
                              key: destinationUrl,
                          },
                      },
                  };
              }
              catch (err) {
                  /* ignore error and attempt query anyway */
              }
          }
          const agQuery = prepareQuery({
              resource: resourceConfig,
              operation: 'retrieve',
              authConfig,
              ...request,
              ...getAdapterFields(),
          }, resourceType);
          if (agQuery.select && agQuery.select.length) {
              const selectStrList = agQuery.select.map((item) => item.property[0]);
              agQuery.select = selectStrList;
          }
          let response;
          if (requestAdapter) {
              response = await driver.executeWithAdapter(agQuery, requestAdapter);
          }
          else {
              response = await driver.execute(agQuery);
          }
          if (!response || response.message) {
              if (resourceType === 'firestore') {
                  let code;
                  let message;
                  let status;
                  try {
                      ({ code, message, status } = response.driverRawError.response.data.error);
                  }
                  catch (e) {
                      message = response.message;
                  }
                  throw ErrorWithData('firestore', message, status, { code });
              }
              if (typeof response === 'object') {
                  throw new ErrorWithData(resourceType, response.message, response.status, response.driverRawError);
              }
              throw new Error('Request failed');
          }
          const record = response.record || response.data || null;
          return record;
      }
      // Load a "REST" data resource
      const operation = resourceConfig.operations.retrieve;
      if (!operation) {
          throw new Error(`Resource ${resourceName} does not support getting a single record`);
      }
      const requestParameters = [...resourceConfig.parameters, ...operation.parameters];
      const headers = {};
      const queryComponents = [];
      let url = `${resourceConfig.base_url}${operation.relative_path || ''}`;
      const adapterFields = getAdapterFields();
      requestParameters.forEach((parameter) => {
          let value = !isAdapter
              ? parameters[parameter.id]
              : get(['identifier', parameter.placement_key], request) ||
                  get(['additionalInputs', parameter.id], adapterFields);
          if (parameter.is_static || typeof value === 'undefined') {
              value = parameter.default;
          }
          if (value == null) {
              // No value for this parameter
              if (parameter.is_optional) {
                  return;
              }
              throw new Error(`Missing required parameter ${JSON.stringify(parameter.title)}`);
          }
          // Map the parameter to headers/URL/query
          const { placement } = parameter;
          const placementKey = parameter.placement_key;
          if (placement === 'header') {
              // Add to headers
              headers[placementKey] = value;
          }
          else if (placement === 'query') {
              // Add to the URL as a query parameter
              queryComponents.push(`${encodeURIComponent(placementKey)}=${encodeURIComponent(String(value))}`);
          }
          else if (placement === 'path') {
              // Replace the placeholder in the URL with escaped string
              url = url.replace(new RegExp(`{\\s*${placementKey}\\s*}`, 'g'), encodeURIComponent(String(value)));
          }
          else if (placement === 'url') {
              // Replace the placeholder in the URL with unescaped string
              url = url.replace(new RegExp(`{\\s*${placementKey}\\s*}`, 'g'), String(value));
          }
      });
      // Add any query parameters to the URL
      if (queryComponents.length) {
          const newQuery = queryComponents.join('&');
          url = url.replace(/(?:\?(.*))?$/, (_, query) => (query ? `?${query}&${newQuery}` : `?${newQuery}`));
      }
      // Perform the request
      const response = await fetch(url, { method: 'GET', headers });
      if (!response.ok) {
          throw response;
      }
      let result;
      let textResponse = false;
      const textRes = response.clone();
      try {
          try {
              result = await response.json();
          }
          catch (e) {
              const text = await textRes.text();
              result = { text_response: text };
              textResponse = true;
          }
      }
      catch (e) {
          result = null;
      }
      const resultPath = operation.response_key_path ? operation.response_key_path.split('.') : [];
      while (result != null && resultPath.length && !textResponse) {
          result = result[resultPath.shift()];
      }
      if (!result) {
          throw new Error(`A ${resourceName} record not found from the response!`);
      }
      return result;
  }
  try {
      const record = await makeRequest();
      if (Array.isArray(record)) {
          const errorStr = 'The server responded with a list for a single data record. The response must be an object.';
          return [
              1,
              {
                  error: {
                      code: 'responseMustBeObject',
                      status: -1,
                      message: errorStr,
                      rawError: new Error(errorStr),
                  },
              },
          ];
      }
      return [0, { record: !isMappedAdapter ? record : importRecord(record, mappingFields) }];
  }
  catch (error) {
      // eslint-disable-next-line no-console
      // console.warn(error);
      if (error.type === 'firestore') {
          return [
              1,
              {
                  error: {
                      code: 'requestFailed',
                      status: error.data.code,
                      message: `${error.status}: ${error.message}`,
                  },
              },
          ];
      }
      // ErrorWithData
      if (['restd', 'odata', 'uql'].includes(error.type)) {
          return [
              1,
              {
                  error: {
                      code: 'requestFailed',
                      status: error.status,
                      message: error.message,
                      rawError: error.data,
                  },
              },
          ];
      }
      let errorStr = String(error);
      if (errorStr.startsWith("Error: Couldn't find data resource")) {
          return [
              1,
              {
                  error: {
                      code: 'resourceNotFound',
                      status: 404,
                      message: errorStr,
                      rawError: error,
                  },
              },
          ];
      }
      if (errorStr === 'TypeError: Failed to fetch') {
          errorStr += '. Does the server allow CORS?';
          return [
              1,
              {
                  error: {
                      code: 'requestFailed',
                      status: error.status,
                      message: errorStr,
                      rawError: error,
                  },
              },
          ];
      }
      if (!error.json) {
          return [
              1,
              {
                  error: {
                      code: 'unknown',
                      status: -1,
                      message: errorStr,
                      rawError: error,
                  },
              },
          ];
      }
      let rawError;
      try {
          const body = await error.json();
          rawError = body;
          errorStr = `JSON error response from server: ${JSON.stringify(body)}.`;
      }
      catch {
          errorStr = 'Non-JSON error body.';
          rawError = { message: errorStr };
      }
      finally {
          // eslint-disable-next-line no-unsafe-finally
          return [
              1,
              {
                  error: {
                      code: 'serverError',
                      status: error.status,
                      message: errorStr,
                      rawError,
                  },
              },
          ];
      }
  }

