  const { getComponentContextProp } = supersonic.context;
  const { getResourceConfig } = supersonic.api.data;
  const get = (p, o) => {
      const isNumber = (v) => typeof v === 'number';
      const isBoolean = (v) => typeof v === 'boolean';
      const isNull = (v) => v === null;
      const isString = (v) => typeof v === 'string';
      return p.reduce((xs, x) => xs && (xs[x] || isNumber(xs[x]) || isBoolean(xs[x]) || isNull(xs[x]) || isString(xs[x])) ? xs[x] : undefined, o);
  };
  class ErrorWithData extends Error {
      constructor(type, message, status, dataOrDriverRawError) {
          super(message);
          this.type = type;
          this.setStatus(status, dataOrDriverRawError);
          this.setData(dataOrDriverRawError);
      }
      setStatus(status, driverRawError) {
          if (status) {
              this.status = status;
          }
          else if (driverRawError && driverRawError.response) {
              this.status = driverRawError.response.status || -1;
          }
          else {
              this.status = -1;
          }
      }
      setData(dataOrDriverRawError) {
          const axiosErrorData = dataOrDriverRawError && dataOrDriverRawError.response && dataOrDriverRawError.response.data;
          this.data = axiosErrorData || dataOrDriverRawError;
      }
  }
  const importRecord = (record, mappingFields = []) => {
      const importedData = {};
      mappingFields.forEach((field) => {
          if (field && field.mapping && field.mapping.length) {
              const value = get(field.mapping, record);
              if (value !== undefined) {
                  importedData[field.propertyName] = value;
              }
          }
      });
      return importedData;
  };
  const setNestedProp = (obj = {}, [first, ...rest], value) => ({
      ...obj,
      [first]: rest.length ? setNestedProp(obj[first], rest, value) : value,
  });
  const exportRecord = (record, mappingFields = []) => {
