              resource: resourceConfig,
              operation: 'update',
              authConfig,
              ...request,
              ...getAdapterFields(),
          }, resourceType);
          if (isMappedAdapter) {
              agQuery = exportResourceConfig(agQuery, mappingFields);
          }
          let response;
          if (requestAdapter) {
              response = await driver.executeWithAdapter(agQuery, requestAdapter);
          }
          else {
              response = await driver.execute(agQuery);
          }
          if (!response || response.message) {
              if (resourceType === 'firestore') {
                  let code;
                  let message;
                  let status;
                  try {
                      ({ code, message, status } = response.driverRawError.response.data.error);
                  }
                  catch (e) {
                      message = response.message;
                  }
                  throw ErrorWithData('firestore', message, status, { code });
              }
              if (typeof response === 'object') {
                  throw new ErrorWithData(resourceType, response.message, response.status, response.driverRawError);
              }
              throw new Error('Request failed');
          }
