      const operation = resourceConfig.operations.replace;
      if (!operation) {
          throw new Error(`Resource ${resourceName} does not support updating records`);
      }
      const requestParameters = [...resourceConfig.parameters, ...operation.parameters];
      const requestSchema = operation.request_schema;
