      const headers = isEmptyResponse ? {} : { 'Content-Type': 'application/json' };
      const body = isEmptyResponse
          ? undefined
          : JSON.stringify(!isMappedAdapter ? properties : exportRecord(request.record, mappingFields));
      const queryComponents = [];
      let url = `${resourceConfig.base_url}${operation.relative_path || ''}`;
      const adapterFields = getAdapterFields();
      requestParameters.forEach((parameter) => {
          let value = !isAdapter
              ? parameters[parameter.id]
              : get(['identifier', parameter.placement_key], request) ||
                  get(['additionalInputs', parameter.id], adapterFields);
          if (parameter.is_static || typeof value === 'undefined') {
              value = parameter.default;
          }
          if (value == null) {
              // No value for this parameter
              if (parameter.is_optional) {
                  return;
              }
              throw new Error(`Missing required parameter ${JSON.stringify(parameter.title)}`);
          }
          // Map the parameter to headers/URL/query
          const { placement } = parameter;
          const placementKey = parameter.placement_key;
          if (placement === 'header') {
              // Add to headers
              headers[placementKey] = value;
          }
          else if (placement === 'query') {
              // Add to the URL as a query parameter
              queryComponents.push(`${encodeURIComponent(placementKey)}=${encodeURIComponent(String(value))}`);
          }
          else if (placement === 'path') {
              // Replace the placeholder in the URL with escaped string
              url = url.replace(new RegExp(`{\\s*${placementKey}\\s*}`, 'g'), encodeURIComponent(String(value)));
          }
          else if (placement === 'url') {
              // Replace the placeholder in the URL with unescaped string
              url = url.replace(new RegExp(`{\\s*${placementKey}\\s*}`, 'g'), String(value));
          }
