const { request, contextId, supersonic } = inputs;
  const { resourceName, parameters = {} } = request;
  const { getComponentContextProp } = supersonic.context;
  const { getResourceConfig } = supersonic.api.data;
  const { prepareExecutable } = supersonic.api.formulas || {};
  const get = (p, o) => {
      const isNumber = (v) => typeof v === 'number';
      const isBoolean = (v) => typeof v === 'boolean';
      const isNull = (v) => v === null;
      const isString = (v) => typeof v === 'string';
      return p.reduce((xs, x) => xs && (xs[x] || isNumber(xs[x]) || isBoolean(xs[x]) || isNull(xs[x]) || isString(xs[x])) ? xs[x] : undefined, o);
  };
  class ErrorWithData extends Error {
      constructor(type, message, status, dataOrDriverRawError) {
          super(message);
          this.type = type;
          this.setStatus(status, dataOrDriverRawError);
          this.setData(dataOrDriverRawError);
      }
      setStatus(status, driverRawError) {
          if (status) {
              this.status = status;
          }
          else if (driverRawError && driverRawError.response) {
              this.status = driverRawError.response.status || -1;
          }
          else {
              this.status = -1;
          }
      }
      setData(dataOrDriverRawError) {
          const axiosErrorData = dataOrDriverRawError && dataOrDriverRawError.response && dataOrDriverRawError.response.data;
          this.data = axiosErrorData || dataOrDriverRawError;
      }
  }
  const importRecord = (record, mappingFields = []) => {
      const importedData = {};
      mappingFields.forEach((field) => {
          if (field && field.mappingExpression && prepareExecutable) {
              try {
                  const expression = prepareExecutable(JSON.parse(field.mappingExpression));
                  importedData[field.propertyName] = expression.execute({ source: { record } });
              }
              catch (error) {
                  throw new Error('An error occured while executing mapping formula');
              }
          }
          else if (field && field.mapping && Array.isArray(field.mapping)) {
              const value = get(field.mapping, record);
              if (value !== undefined) {
                  importedData[field.propertyName] = value;
              }
          }
      });
      return importedData;
  };
  const adapterPrefix = 'adapter';
  const [prefix, boundResource] = (resourceName || '').split(':');
  const isAdapter = prefix === adapterPrefix;
  const getActualResourceName = () => {
      if (isAdapter) {
          const adapterProp = getComponentContextProp(contextId.rootContextId, boundResource, contextId, true);
          return get(['mapping', 'value'], adapterProp) || get(['value'], adapterProp) || resourceName;
      }
      return resourceName;
  };
  const getMapping = () => isAdapter
      ? get(['mapping', 'fields'], getComponentContextProp(contextId.rootContextId, boundResource, contextId, true))
      : null;
  const getAdapterFields = () => {
      if (isAdapter) {
          const adapterProp = getComponentContextProp(contextId.rootContextId, boundResource, contextId, true);
          return ['pagination', 'order', 'search', 'conditions', 'select', 'authConfig', 'additionalInputs'].reduce((adapterFields, field) => {
              const value = get(['mapping', field], adapterProp) || get([field], adapterProp);
              return value ? { ...adapterFields, [field === 'conditions' ? 'condition' : field]: value } : adapterFields;
          }, {});
      }
      return {};
  };
  const mappingFields = getMapping(resourceName);
  const isMappedAdapter = isAdapter && !!mappingFields;
  const implementsCsrf = (driverConfiguration) => (driverConfiguration.base && driverConfiguration.base.fetchCsrfToken) || false;
  async function getRequestAdapterInfo(btpDestinationName, driverConfiguration) {
      const { sapBtp } = supersonic.api;
      const btpDriverAdapter = sapBtp.getDriverAdapter && sapBtp.getDriverAdapter(btpDestinationName);
      if (btpDriverAdapter) {
          return { adapter: btpDriverAdapter, isProxyAdapter: true };
      }
      const serviceImplementsCsrf = implementsCsrf(driverConfiguration);
      const appRouterCsrfAdapter = !serviceImplementsCsrf && sapBtp.getAppRouterCsrfAdapter && (await sapBtp.getAppRouterCsrfAdapter());
      if (appRouterCsrfAdapter) {
