              }
          }
          const agQuery = {
              ...request,
              resource: resourceConfig,
              operation: 'list',
              authConfig,
              count: request.pagination && request.pagination.includeTotalCount,
              ...getAdapterFields(),
          };
          if (agQuery.select && agQuery.select.length) {
              const selectStrList = agQuery.select.map((item) => item.property[0]);
              agQuery.select = selectStrList;
          }
          let response;
          if (requestAdapter) {
