const { request, contextId, supersonic } = inputs;
const {
  resourceName,
  id,
  properties = {},
  parameters = {},
} = request;

const { getComponentContextProp } = supersonic.context;
const { getResourceConfig } = supersonic.api.data;
const { prepareExecutable } = supersonic.api.formulas || {};

const get = (p, o) => {
  const isNumber = (v) => typeof v === 'number';
  const isBoolean = (v) => typeof v === 'boolean';
  const isNull = (v) => v === null;
  const isString = (v) => typeof v === 'string';
  return p.reduce((xs, x) => (
    xs && (xs[x] || isNumber(xs[x]) || isBoolean(xs[x]) || isNull(xs[x]) || isString(xs[x])) ? xs[x] : undefined
  ), o);
};

const importRecord = (record, mappingFields = []) => {
  const importedData = {};
  mappingFields.forEach((field) => {
    if (field && field.mappingExpression && prepareExecutable) {
      try {
        const expression = prepareExecutable(JSON.parse(field.mappingExpression));
        importedData[field.propertyName] = expression.execute({ source: { record } });
      } catch (error) {
        throw new Error('An error occured while executing mapping formula.');
      }
    } else if (field && field.mapping && Array.isArray(field.mapping)) {
      const value = get(field.mapping, record);
      if (value !== undefined) {
        importedData[field.propertyName] = value;
      }
    }
  });
  return importedData;
};

const setNestedProp = (obj = {}, [first, ...rest], value) => ({
  ...obj,
  [first]: rest.length
    ? setNestedProp(obj[first], rest, value)
    : value,
});

const exportRecord = (record, mappingFields = []) => {
  let exportedRecord = {};
  mappingFields.forEach((field) => {
    if (field && field.mapping && Array.isArray(field.mapping)) {
      exportedRecord = setNestedProp(exportedRecord, field.mapping, get([field.propertyName], record));
    }
  });
  return exportedRecord;
};

const exportResourceConfig = (resourceConfig, mappingFields) => {
  const exportedResourceConfig = { ...resourceConfig };
  if (resourceConfig.record) {
    exportedResourceConfig.record = exportRecord(resourceConfig.record, mappingFields);
  }
  return exportedResourceConfig;
};

const adapterPrefix = 'adapter';
const [prefix, boundResource] = (resourceName || '').split(':');
const isAdapter = prefix === adapterPrefix;

const getActualResourceName = () => {
  if (isAdapter) {
    const adapterProp = getComponentContextProp(contextId.rootContextId, boundResource, contextId, true);
    return (
      get(['mapping', 'value'], adapterProp) || get(['value'], adapterProp) || resourceName
    );
  }
  return resourceName;
};

const getMapping = () => (
  isAdapter
    ? get(['mapping', 'fields'], getComponentContextProp(contextId.rootContextId, boundResource, contextId, true))
    : null
);

const getAdapterFields = () => {
  if (isAdapter) {
    const adapterProp = getComponentContextProp(contextId.rootContextId, boundResource, contextId, true);
    return ['identifier', 'authConfig', 'additionalInputs'].reduce((adapterFields, field) => {
      const value = get(['mapping', field], adapterProp) || get([field], adapterProp);
      return value ? { ...adapterFields, [field]: value } : adapterFields;
    }, {});
  }
  return {};
};

// TODO: should be handled in odata driver in a future version of
// api-configurator using string schema format field set to "uuid"
const uuidMatcher = /^[0-9a-f]{8}-[0-9a-f]{4}-[1-5][0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;
const fixIdentifierValue = (value) => (value && typeof value === 'string' && !uuidMatcher.test(value) ? `'${value}'` : value);
const prepareQuery = (query, resourceType) => {
  if (resourceType === 'odata' && query.identifier && Object.keys(query.identifier).length > 1) {
    return { ...query, identifier: Object.keys(query.identifier).reduce((acc, key) => ({ ...acc, [key]: fixIdentifierValue(query.identifier[key]) }), {}) };
  }
  return query;
};

const mappingFields = getMapping(resourceName);
const isMappedAdapter = isAdapter && !!mappingFields;

async function makeRequest() {
  const resourceConfig = { ...getResourceConfig(getActualResourceName(resourceName)) };
  const { driverConfiguration = {} } = resourceConfig;
  const resourceType = resourceConfig.type || driverConfiguration.type;

  if (!resourceConfig) {
    throw new Error(`Couldn't find data resource with given name: ${resourceName}.`);
  }

  if (resourceType === 'agdb') {
    const data = await supersonic.resource.update(resourceConfig.agdb_id, { object: properties }, id);
    return data && data.value && data.value.object;
  }
  // Update a local data resource
  if (resourceType === 'local') {
    if (id == null) {
      throw new Error(`Missing ${resourceName} record ID`);
    }
    // Omit the 'id' from the properties in case it has it
    const updatedProperties = { ...properties };
    delete updatedProperties.id;
    const { appId } = await supersonic.api.app.getAppId();
    const key = `local-resource:${appId}:${resourceName}:${id}`;
    const { data: record } = await supersonic.api.asyncStorage.getItem({ key });
    if (record == null || typeof record !== 'object' || typeof record.id === 'undefined') {
      throw new Error(`Record ${resourceName} with ID ${id} does not exist`);
    }
    const updatedRecord = { ...record, ...updatedProperties };
    await supersonic.api.asyncStorage.setItem({ key, data: updatedRecord });
    return updatedRecord;
  }
  const parseBtpDestinationName = () => {
    if (!driverConfiguration.base || !driverConfiguration.base.baseURL) return undefined;
    const { baseURL } = driverConfiguration.base;
    if (baseURL.source !== 'expression') return undefined;
    const match = baseURL.key.formula.match(/connectedSystem\.btp\.applicationBaseUrl \+ '(.+)'/);
    if (!match) return undefined;
    return match[1];
  };
  const btpDestinationName = parseBtpDestinationName();
  if (supersonic.api.data.drivers && resourceType in supersonic.api.data.drivers) {
    const driver = supersonic.api.data.drivers[resourceType];
    if (!driver.execute) {
      throw new Error(`Invalid or missing driver for Resource ${resourceName}`);
    }
    let { authConfig } = request;
    if (resourceType === 'firestore') {
      try {
        const { firebase } = supersonic.api;
        const currentUser = await new Promise((resolve, reject) => {
          const unsubscribe = firebase.auth().onAuthStateChanged((user) => {
            unsubscribe();
            resolve(user);
          }, reject);
        });
        const token = await currentUser.getIdToken();
        authConfig = {
          type: 'bearer',
          authToken: token,
        };
      } catch (err) {
        /* ignore error and attempt query anyway */
      }
    } else if (['restd', 'odata'].includes(resourceType) && btpDestinationName) {
      try {
        const { sapBtp } = supersonic.api;
        const token = await sapBtp.getAccessToken();
        authConfig = {
          type: 'bearer',
          authToken: token,
        };
        const btpBaseURL = await sapBtp.resolveDestinationUrl(btpDestinationName);
        resourceConfig.driverConfiguration = {
          ...resourceConfig.driverConfiguration,
          base: {
            ...resourceConfig.driverConfiguration.base,
            baseURL: {
              source: 'str',
              key: btpBaseURL,
            },
          },
        };
      } catch (err) {
