      resource: resourceConfig,
      operation: 'update',
      authConfig,
      ...request,
      ...getAdapterFields(),
    }, resourceType);
    if (isMappedAdapter) {
      agQuery = exportResourceConfig(agQuery, mappingFields);
    }
    const response = await driver.execute(agQuery);
    if (!response || response.message) {
      if (resourceType === 'firestore') {
        let code; let message; let status;
        try {
          ({ code, message, status } = response.driverRawError.response.data.error);
        } catch (e) {
          message = response.message;
        }
        // eslint-disable-next-line no-throw-literal
        throw {
          type: 'firestore',
          code,
          message,
          status,
        };
      }
      throw new Error(response ? response.message : 'Request failed');
    }
    const record = response.record || response.data || null;
    return record;
  }
  // Update a "REST" data resource
  const operation = resourceConfig.operations.replace;
  if (!operation) {
    throw new Error(`Resource ${resourceName} does not support updating records`);
  }
  const requestParameters = [
    ...resourceConfig.parameters,
    ...operation.parameters,
  ];
  const requestSchema = operation.request_schema;
  const isEmptyResponse = !properties || (requestSchema != null && requestSchema.type === 'undefined');
  const headers = isEmptyResponse ? {} : { 'Content-Type': 'application/json' };
  const body = isEmptyResponse ? undefined : JSON.stringify(!isMappedAdapter ? properties : exportRecord(request.record, mappingFields));
  const queryComponents = [];
  let url = `${resourceConfig.base_url}${operation.relative_path || ''}`;
  const adapterFields = getAdapterFields();
  requestParameters.forEach((parameter) => {
    let value = !isAdapter ? parameters[parameter.id]
      : (get(['identifier', parameter.placement_key], request) || get(['additionalInputs', parameter.id], adapterFields));
    if (parameter.is_static || typeof value === 'undefined') {
      value = parameter.default;
    }
    if (value == null) {
    // No value for this parameter
      if (parameter.is_optional) {
        return;
      }
      throw new Error(`Missing required parameter ${JSON.stringify(parameter.title)}`);
    }
    // Map the parameter to headers/URL/query
    const { placement } = parameter;
    const placementKey = parameter.placement_key;
    if (placement === 'header') {
      // Add to headers
      headers[placementKey] = value;
    } else if (placement === 'query') {
      // Add to the URL as a query parameter
      queryComponents.push(`${encodeURIComponent(placementKey)}=${encodeURIComponent(String(value))}`);
    } else if (placement === 'path') {
      // Replace the placeholder in the URL with escaped string
      url = url.replace(new RegExp(`{\\s*${placementKey}\\s*}`, 'g'), encodeURIComponent(String(value)));
    } else if (placement === 'url') {
      // Replace the placeholder in the URL with unescaped string
      url = url.replace(new RegExp(`{\\s*${placementKey}\\s*}`, 'g'), String(value));
    }
  });
  // Add any query parameters to the URL
  if (queryComponents.length) {
    const newQuery = queryComponents.join('&');
    url = url.replace(/(?:\?(.*))?$/, (_, query) => (query ? `?${query}&${newQuery}` : `?${newQuery}`));
  }
  // Perform the request
  const method = operation.method || 'PUT';
  const response = await fetch(url, { method, headers, body });
  if (!response.ok) {
    throw response;
  }

  let result;
  let textResponse = false;
  const textRes = response.clone();

  try {
    try {
      result = await response.json();
    } catch (e) {
      const text = await textRes.text();
      result = { text_response: text };
      textResponse = true;
    }
  } catch (e) {
    result = null;
  }

  const resultPath = operation.response_key_path ? operation.response_key_path.split('.') : [];
  while (result != null && resultPath.length && !textResponse) {
    result = result[resultPath.shift()];
  }
  return result;
}

try {
  const response = await makeRequest();
  return [0, { response: !isMappedAdapter ? response : importRecord(response, mappingFields) }];
} catch (error) {
  // eslint-disable-next-line no-console
  // console.warn(error);
  if (error.type === 'firestore') {
    return [1, {
      error: {
        code: 'requestFailed',
        status: error.code,
        message: `${error.status}: ${error.message}`,
      },
    }];
  }

  let errorStr = String(error);

  if (errorStr.startsWith('Error: Couldn\'t find data resource')) {
    return [1, {
      error: {
        code: 'resourceNotFound',
        status: 404,
        message: errorStr,
        rawError: error,
      },
    }];
  }

  if (errorStr === 'TypeError: Failed to fetch') {
    errorStr += '. Does the server allow CORS?';
    return [1, {
      error: {
        code: 'requestFailed',
        status: error.status,
        message: errorStr,
        rawError: error,
      },
    }];
  } if (!error.json) {
    return [1, {
      error: {
        code: 'unknown',
        status: -1,
        message: errorStr,
        rawError: error,
      },
    }];
  }
  let rawError;
  try {
    const body = await error.json();
    rawError = body;
    errorStr = `JSON error response from server: ${JSON.stringify(body)}.`;
  } catch {
    errorStr = 'Non-JSON error body.';
    rawError = { message: errorStr };
  } finally {
    // eslint-disable-next-line no-unsafe-finally
    return [1, {
      error: {
        code: 'serverError',
        status: error.status,
        message: errorStr,
        rawError,
      },
    }];
  }
}

