f0bf1a1a.10aa3.func:
const { request, contextId, supersonic } = inputs;
const { resourceName, parameters = {} } = request;

const { getComponentContextProp } = supersonic.context;
const { getResourceConfig } = supersonic.api.data;
const { prepareExecutable } = supersonic.api.formulas || {};

const get = (p, o) => {
  const isNumber = (v) => typeof v === 'number';
  const isBoolean = (v) => typeof v === 'boolean';
  const isNull = (v) => v === null;
  const isString = (v) => typeof v === 'string';
  return p.reduce((xs, x) => (
    xs && (xs[x] || isNumber(xs[x]) || isBoolean(xs[x]) || isNull(xs[x]) || isString(xs[x])) ? xs[x] : undefined
  ), o);
};

const importRecord = (record, mappingFields = []) => {
  const importedData = {};
  mappingFields.forEach((field) => {
    if (field && field.mappingExpression && prepareExecutable) {
      try {
        const expression = prepareExecutable(JSON.parse(field.mappingExpression));
        importedData[field.propertyName] = expression.execute({ source: { record } });
      } catch (error) {
        throw new Error('An error occured while executing mapping formula');
      }
    } else if (field && field.mapping && Array.isArray(field.mapping)) {
      const value = get(field.mapping, record);
      if (value !== undefined) {
        importedData[field.propertyName] = value;
      }
    }
  });
  return importedData;
};
const adapterPrefix = 'adapter';
const [prefix, boundResource] = (resourceName || '').split(':');
const isAdapter = prefix === adapterPrefix;

const getActualResourceName = () => {
  if (isAdapter) {
    const adapterProp = getComponentContextProp(contextId.rootContextId, boundResource, contextId, true);
    return (
      get(['mapping', 'value'], adapterProp) || get(['value'], adapterProp) || resourceName
    );
  }
  return resourceName;
};

const getMapping = () => (
  isAdapter
    ? get(['mapping', 'fields'], getComponentContextProp(contextId.rootContextId, boundResource, contextId, true))
    : null
);

const getAdapterFields = () => {
  if (isAdapter) {
    const adapterProp = getComponentContextProp(contextId.rootContextId, boundResource, contextId, true);
    return [
      'pagination',
      'order',
      'search',
      'conditions',
      'authConfig',
      'additionalInputs',
    ].reduce((adapterFields, field) => {
      const value = get(['mapping', field], adapterProp) || get([field], adapterProp);
      return value ? { ...adapterFields, [field === 'conditions' ? 'condition' : field]: value } : adapterFields;
    }, {});
  }
  return {};
};

const mappingFields = getMapping(resourceName);
const isMappedAdapter = isAdapter && !!mappingFields;

async function makeRequest() {
  const resourceConfig = { ...getResourceConfig(getActualResourceName(resourceName)) };
  const { driverConfiguration = {} } = resourceConfig;
  const resourceType = resourceConfig.type || driverConfiguration.type;

  if (!resourceConfig) {
    throw new Error(`Couldn't find data resource with given name: ${resourceName}.`);
  }

  if (resourceType === 'agdb') {
    // This is assumed to be a legacy data resource
    const query = parameters.query || {};
    // AGDB -related parameters are added to the query in a special way
    const { sort } = parameters;
    const { filters } = parameters;
    const { limit } = parameters;
    const { skip } = parameters;

    if (sort != null) {
      query.sort = JSON.stringify(sort);
    }
    if (filters != null) {
      query.query = JSON.stringify(filters);
    }
    if (limit != null) {
      query.limit = limit;
    }
    if (skip != null) {
      query.skip = skip;
    }

    const backendResourceName = (resourceConfig && resourceConfig.agdb_id) || resourceName;
    const data = await supersonic.resource.findAll(backendResourceName, query);
    return { records: (data && data.value && data.value.objects) || [] };
  }
  // Load a local data resource
  if (resourceType === 'local') {
    const { appId } = await supersonic.api.app.getAppId();
    const keyPrefix = `local-resource:${appId}:${resourceName}:`;
    const { data: allKeys } = await supersonic.api.asyncStorage.getAllKeys();
    const keys = allKeys.filter((key) => key.startsWith(keyPrefix));
    const { data: results } = await supersonic.api.asyncStorage.multiGet({ data: keys });
    const records = (results || [])
      .map(([, record]) => {
        if (typeof record === 'string') {
          try {
            return JSON.parse(record);
          } catch (error) {
            return null;
          }
        }
        return record;
      })
      .filter((record) => record != null && typeof record === 'object' && typeof record.id !== 'undefined');
    return { records };
  }
  const parseBtpDestinationName = () => {
    if (!driverConfiguration.base || !driverConfiguration.base.baseURL) return undefined;
    const { baseURL } = driverConfiguration.base;
    if (baseURL.source !== 'expression') return undefined;
    const match = baseURL.key.formula.match(/connectedSystem\.btp\.applicationBaseUrl \+ '(.+)'/);
    if (!match) return undefined;
    return match[1];
  };
  const btpDestinationName = parseBtpDestinationName();
  if (supersonic.api.data.drivers && resourceType in supersonic.api.data.drivers) {
    const driver = supersonic.api.data.drivers[resourceType];
    if (!driver.execute) {
      throw new Error(`Invalid or missing driver for Resource ${resourceName}`);
    }
    let { authConfig } = request;
    if (resourceType === 'firestore') {
      try {
        const { firebase } = supersonic.api;
        const currentUser = await new Promise((resolve, reject) => {
          const unsubscribe = firebase.auth().onAuthStateChanged((user) => {
            unsubscribe();
            resolve(user);
          }, reject);
        });
        const token = await currentUser.getIdToken();
        authConfig = {
          type: 'bearer',
          authToken: token,
        };
      } catch (err) {
        /* ignore error and attempt query anyway */
      }
    } else if (['restd', 'odata'].includes(resourceType) && btpDestinationName) {
      try {
        const { sapBtp } = supersonic.api;
        const token = await sapBtp.getAccessToken();
        authConfig = {
          type: 'bearer',
          authToken: token,
        };
        const btpBaseURL = await sapBtp.resolveDestinationUrl(btpDestinationName);
        resourceConfig.driverConfiguration = {
          ...resourceConfig.driverConfiguration,
          base: {
            ...resourceConfig.driverConfiguration.base,
            baseURL: {
              source: 'str',
              key: btpBaseURL,
            },
          },
        };
      } catch (err) {
        /* ignore error and attempt query anyway */
      }
    }
    const agQuery = {
      ...request,
      resource: resourceConfig,
      operation: 'list',
      authConfig,
      count: request.pagination && request.pagination.includeTotalCount,
      ...getAdapterFields(),
    };
    const response = await driver.execute(agQuery);

    if (!response || response.message) {
      if (resourceType === 'firestore') {
        let code; let message; let status;
        try {
          ({ code, message, status } = response.driverRawError.response.data[0].error);
        } catch (e) {
          message = response.message;
        }
        // eslint-disable-next-line no-throw-literal
        throw {
          type: 'firestore',
          code,
          message,
          status,
        };
      }
      throw new Error(response ? response.message : 'Request failed');
    }

    if (!response || response.message) {
      throw new Error(response ? response.message : 'Request failed');
    }
    const records = response.records || response.data || [];
    return { records, totalCount: response.totalCount };
  }
  // Load a "REST" data resource
  const operation = resourceConfig.operations.list;
  if (!operation) {
    throw new Error(`Resource ${resourceName} does not support getting record collections`);
  }
  const requestParameters = [
    ...resourceConfig.parameters,
    ...operation.parameters,
  ];
  const headers = {};
  const queryComponents = [];
  let url = `${resourceConfig.base_url}${operation.relative_path || ''}`;
  const adapterFields = getAdapterFields();
  requestParameters.forEach((parameter) => {
    let value = !isAdapter ? parameters[parameter.id]
      : get(['additionalInputs', parameter.id], adapterFields);
    if (parameter.is_static || typeof value === 'undefined') {
      value = parameter.default;
    }
    if (value == null) {
      // No value for this parameter
      if (parameter.is_optional) {
        return;
      }
      throw new Error(`Missing required parameter ${JSON.stringify(parameter.title)}`);
    }
    // Map the parameter to headers/URL/query
    const { placement } = parameter;
    const placementKey = parameter.placement_key;
    if (placement === 'header') {
      // Add to headers
      headers[placementKey] = value;
    } else if (placement === 'query') {
      // Add to the URL as a query parameter
      queryComponents.push(`${encodeURIComponent(placementKey)}=${encodeURIComponent(String(value))}`);
    } else if (placement === 'path') {
      // Replace the placeholder in the URL with escaped string
      url = url.replace(new RegExp(`{\\s*${placementKey}\\s*}`, 'g'), encodeURIComponent(String(value)));
    } else if (placement === 'url') {
      // Replace the placeholder in the URL with unescaped string
      url = url.replace(new RegExp(`{\\s*${placementKey}\\s*}`, 'g'), String(value));
    }
  });
  // Add any query parameters to the URL
  if (queryComponents.length) {
    const newQuery = queryComponents.join('&');
    url = url.replace(/(?:\?(.*))?$/, (_, query) => (query ? `?${query}&${newQuery}` : `?${newQuery}`));
  }
  // Perform the request
  const response = await fetch(url, { method: 'GET', headers });
  if (!response.ok) {
    throw response;
  }
  const responseJson = await response.json();
  let results = responseJson;
  const resultPath = operation.response_key_path ? operation.response_key_path.split('.') : [];
  while (results != null && resultPath.length) {
    results = results[resultPath.shift()];
  }
  if (!Array.isArray(results)) {
    throw new Error(`
The result was not an array. Maybe the data you're looking for is inside one of the response object keys?

${JSON.stringify(responseJson, null, 2)}
`);
  }
  return { records: results };
}

try {
  const { records, totalCount } = await makeRequest();
  return [0, {
    records: !isMappedAdapter ? records : records.map((record) => importRecord(record, mappingFields)),
    totalCount,
  }];
} catch (error) {
  // eslint-disable-next-line no-console
  // console.warn(error);
  if (error.type === 'firestore') {
    return [1, {
      error: {
        code: 'requestFailed',
        status: error.code,
        message: `${error.status}: ${error.message}`,
      },
    }];
  }

  let errorStr = String(error);

  if (errorStr.startsWith('Error: Couldn\'t find data resource')) {
    return [1, {
      error: {
        code: 'resourceNotFound',
        status: 404,
        message: errorStr,
        rawError: error,
      },
    }];
  }
  if (errorStr === 'TypeError: Failed to fetch') {
    errorStr += '. Does the server allow CORS?';
    return [1, {
      error: {
        code: 'requestFailed',
        status: error.status,
        message: errorStr,
        rawError: error,
      },
    }];
  } if (!error.json) {
    return [1, {
      error: {
        code: 'unknown',
        status: -1,
        message: errorStr,
        rawError: error,
      },
    }];
  }
  let rawError;
  try {
    const body = await error.json();
    rawError = body;
    errorStr = `JSON error response from server: ${JSON.stringify(body)}.`;
  } catch {
    errorStr = 'Non-JSON error body.';
    rawError = { message: errorStr };
  } finally {
    // eslint-disable-next-line no-unsafe-finally
    return [1, {
      error: {
        code: 'serverError',
        status: error.status,
        message: errorStr,
        rawError,
      },
    }];
  }
}

